import React from "react"
import { connect } from "react-redux";
import { useDispatch, useSelector } from 'react-redux';
import displayMessages from '../messages_action';


const GET_MESSAGES_REQUEST = 'GET_MESSAGES_REQUEST';
import PropTypes from "prop-types";

function getMessages() {
  console.log('getMessages() Action!!')
  return {
    type: GET_MESSAGES_REQUEST
  };
};

async function fetchApi() {
   const response = await fetch('http://localhost:3000/api/messages/1')
     const json = await response.json()
     return json
}


class HelloWord extends React.Component {
  // dispatch = useDispatch();
  componentDidMount() {
    const fetched = fetchApi().then( res => {
      dispatch(displayMessages(res.data.body))
    })
  }

  render () {
    return (
      <React.Fragment>
        Greeting: {this.props.greeting}
        <button className="getMessageBtn" onClick={() => this.props.getMessages()}>getMessages</button>

      </React.Fragment>
    );
  }
}

const structuredSelector = createStructuredSelector({
  messages: state => state.messages,
});

HelloWord.propTypes = {
  greeting: PropTypes.string
};

const mapDispatchToProps = { getMessages };


export default connect(structuredSelector, mapDispatchToProps)(HelloWord);
